import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

const RestaurantProfile = () => {
  const { id } = useParams();
  const [restaurant, setRestaurant] = useState([]);

  useEffect(() => {
    fetchRestaurantById(id);
  }, [id]);


  async function fetchRestaurantById(id) {
    try {
      console.log("resssss",id)
      const response = await axios.get(`http://localhost:8080/api/restaurants/${id}`);
      setRestaurant(response.data)
      console.log(response.data)
    } catch (error) {
      console.error("Failed to fetch restaurant:", error);
      throw error; // Rethrow the error to handle it further up the call stack
    }
  }

  if (!restaurant) {
    return <div>Loading...</div>;
  }


  return (
    <div>
         <Card style={{ width: '50rem' }}>
      <Card.Img variant="top" src={restaurant.image} />
      <Card.Body>
        <Card.Title><h1>{restaurant.name}</h1></Card.Title>
        <Card.Text>
        Location:{restaurant.location}
        <h2>Rating: {restaurant.rating}</h2><br/>
        <h2>Description: {restaurant.description}</h2><br/>
        <h2>Upload Date: {new Date(restaurant.upload_date).toLocaleDateString()}</h2>
        </Card.Text>
        <Card.Text>
          Some quick example text to build on the card title and make up the
          bulk of the card's content.
        </Card.Text>
        <Button variant="primary">Go somewhere</Button>
      </Card.Body>
    </Card>
      {/* <h1>{restaurant.name}</h1>
      {restaurant.image && <img src={restaurant.image} alt="Restaurant" />}
      <h2>Location: {restaurant.location}</h2> <br/>
      <h2>Rating: {restaurant.rating}</h2><br/>
      <h2>Description: {restaurant.description}</h2><br/>
      <h2>Upload Date: {new Date(restaurant.upload_date).toLocaleDateString()}</h2>
      <br/> */}
    </div>
  );
};

export default RestaurantProfile;
