import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Button, Table, Form, FormControl, Row, Col } from 'react-bootstrap';
import Pagination from 'react-bootstrap/Pagination';
import MyNavbar from './components/Navbar';
import RestaurantEditModal from './components/RestaurantEditModal';
import RestaurantProfile from './components/restaurantProfile'; // Adjust the import according to your project structure
// import { Routes } from './Routes';
import RestaurantList from './components/restaurantList';
import { BrowserRouter as Router, Routes, Route} from 'react-router-dom';


const App = () => {
  //restaurants- represents list of restaurants
  const [restaurants, setRestaurants] = useState([]);
  // new restaurant - to add new restaurant
  const [newRestaurant, setNewRestaurant] = useState(null);
  // edit function for restaurant details
  const [editing, setEditing] = useState(false);
  //currentRestaurant is for Update Modal
  const [currentRestaurant, setCurrentRestaurant] = useState({});
  // handle saving and closing the popup form
  const [showModal, setShowModal] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  // Initial sorting order
  const [sortOrder, setSortOrder] = useState('asc'); 
  //Button visibility
  const [showActions, setShowActions] = useState(false);

  useEffect(() => {
    fetchRestaurants();
  }, []);

  // Calculate the indexes for the current items
  const itemsPerPage = 10;
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  //Current Items are just the restaurants in each pages, not the full list
  const currentItems = restaurants.slice(indexOfFirstItem, indexOfLastItem);
  const [activeRowId, setActiveRowId] = useState(null); // Initialize activeRowId state
  const totalPages = Math.ceil(restaurants.length / itemsPerPage);
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  //Sort by Name
  const sortByName = () => {
    let sortedItems;
    if (sortOrder === 'asc') {
      sortedItems = [...restaurants].sort((a, b) => a.name.localeCompare(b.name));
    } else {
      sortedItems = [...restaurants].sort((a, b) => b.name.localeCompare(a.name));
    }
    setRestaurants(sortedItems);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc'); // Toggle sortOrder
  };

  const sortByDate = () => {
    let sortedItems;
    if (sortOrder === 'asc') {
      sortedItems = [...restaurants].sort((a, b) => new Date(a.upload_date) - new Date(b.upload_date));
    } else {
      sortedItems = [...restaurants].sort((a, b) => new Date(b.upload_date) - new Date(a.upload_date));
    }
    setRestaurants(sortedItems);
    setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc'); // Toggle sortOrder
  };

  const fetchRestaurants = async () => {
    const response = await axios.get('http://localhost:8080/api/restaurants');
    setRestaurants(response.data);
  };

  const addRestaurant = () => {
    setNewRestaurant({ name: '', location: '', rating: '', upload_date: new Date().toISOString().split('T')[0]});
  };

  const saveNewRestaurant = async () => {
    const response = await axios.post('http://localhost:8080/api/restaurants', newRestaurant);
    setRestaurants([...restaurants, response.data]);
    setNewRestaurant(null);
  };

  const updateRestaurant = async (updatedRestaurant) => {
    updatedRestaurant.upload_date = new Date().toISOString().split('T')[0];
    console.log(updatedRestaurant.upload_date)
    const response = await axios.put(`http://localhost:8080/api/restaurants/${updatedRestaurant.id}`, updatedRestaurant);
    setRestaurants(restaurants.map(restaurant => (restaurant.id === updatedRestaurant.id ? response.data : restaurant)));
    setEditing(false);
  };

  const deleteRestaurant = async (id) => {
    await axios.delete(`http://localhost:8080/api/restaurants/${id}`);
    setRestaurants(restaurants.filter(restaurant => restaurant.id !== id));
  };

  const startEditing = (restaurant) => {
    setEditing(true);
    setCurrentRestaurant(restaurant);
    setShowModal(true);
  };

  const handleSave = (updatedRestaurant) => {
    updateRestaurant(updatedRestaurant);
    setShowModal(false);
  };

  const handleClose = () => {
    setShowModal(false);
  };


  return (
    <div>
              <Routes>
          <Route 
            path="/" 
            element={
              <RestaurantList
                restaurants={restaurants}
                newRestaurant={newRestaurant}
                setNewRestaurant={setNewRestaurant}
                currentPage={currentPage}
                setCurrentPage={setCurrentPage}
                totalPages={totalPages}
                paginate={paginate}
                sortByName={sortByName}
                sortByDate={sortByDate}
                activeRowId={activeRowId}
                setActiveRowId={setActiveRowId}
                startEditing={startEditing}
                deleteRestaurant={deleteRestaurant}
                addRestaurant={addRestaurant}
                saveNewRestaurant={saveNewRestaurant}
              />
            } 
          />
          <Route path="/restaurants/:id" element={<RestaurantProfile />} />
        </Routes>
      <MyNavbar />
      <Container className="mt-5">
        <tb>aaaaaaabbbbbbbbbbbbbbbb</tb>
      <img 
          src="/restaurant-list-burger.png" 
          alt="Restaurant Burger" 
          className="stretched-img mb-4"
        />
        <Table striped bordered hover className="mt-4 custom-table">
          <thead>
            <tr>
              <th className="table-name-width" style={{ cursor: 'pointer', color: 'black' }} onClick={sortByName}>↨  Name</th>
              <th className="table-name-width">Location</th>
              <th className="table-width" style={{ cursor: 'pointer', color: 'black' }} onClick={sortByDate}>↨ Upload Date</th>
              <th className="table-width">Rating</th>
              <th className="table-width">Actions</th>
            </tr>
          </thead>
          <tbody>
          {currentItems.map((restaurant) => (
  <React.Fragment key={restaurant.id}>
    <tr>
      <td>
        {/* Correctly placing the Link outside of the <tr> and inside a <td> */}
        <Link to={`/restaurants/${restaurant.id}`}>{restaurant.name}</Link>
      </td>
      <td>{restaurant.location}</td>
      <td>{new Date(restaurant.upload_date).toISOString().substring(0, 10)}</td>
      <td>{restaurant.rating}</td>
      <td>
        <Button
          variant="outline-secondary"
          className="mr-3 btn-grey"
          onClick={() => setActiveRowId(activeRowId === restaurant.id ? null : restaurant.id)}
        >
          <span className="material-icons-outlined">...</span> {/* Corrected icon for three dots */}
        </Button>
        {activeRowId === restaurant.id && (
          <div className="dropdown-menu show">
            <Button
              variant="info"
              className="mr-3 btn-darkpink"
              onClick={() => {
                startEditing(restaurant);
                setActiveRowId(null);
              }}
            >
              Edit
            </Button>
            <Button
              className="mr-3 btn-orange"
              variant="secondary"
              onClick={() => {
                deleteRestaurant(restaurant.id);
                setActiveRowId(null);
              }}
            >
              Delete
            </Button>
          </div>
        )}
      </td>
    </tr>
  </React.Fragment>
))}
            {newRestaurant && (
              <tr>
                <Form>
                <td>

                  <FormControl
                    type="text"
                    placeholder="Enter restaurant name"
                    value={newRestaurant.name}
                    onChange={(e) => setNewRestaurant({ ...newRestaurant, name: e.target.value })}
                  />
                </td>
                <td>
                  <FormControl
                    type="text"
                    placeholder="Enter location"
                    value={newRestaurant.location}
                    onChange={(e) => setNewRestaurant({ ...newRestaurant, location: e.target.value })}
                  />
                </td>
                <td>
                  <FormControl
                    type="number"
                    placeholder="Enter rating"
                    value={newRestaurant.rating}
                    onChange={(e) => setNewRestaurant({ ...newRestaurant, rating: e.target.value })}
                  />
                </td>
                </Form>
              </tr>
            )}
          </tbody>
        </Table>
        <Row className="mb-4">
          <Col>
        <Button
            className="mb-3 btn-pink"
            onClick={addRestaurant}
            size="lg"
          >
            Add Restaurant
          </Button>
        </Col>
        <Col className="d-flex justify-content-end">
        <Pagination>
            <Pagination.First onClick={() => paginate(1)} />
            <Pagination.Prev
              onClick={() => currentPage > 1 && paginate(currentPage - 1)}
            />
            {[...Array(totalPages)].map((_, index) => (
              <Pagination.Item
                key={index + 1}
                active={index + 1 === currentPage}
                onClick={() => paginate(index + 1)}
              >
                {index + 1}
              </Pagination.Item>
            ))}
            <Pagination.Next
              onClick={() => currentPage < totalPages && paginate(currentPage + 1)}
            />
            <Pagination.Last onClick={() => paginate(totalPages)} />
          </Pagination>
        </Col>
        </Row>
      </Container>
      <RestaurantEditModal
        show={showModal}
        handleClose={handleClose}
        restaurant={currentRestaurant}
        handleSave={handleSave}
      />
    </div>
  );
};

export default App;
