import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [name, setName] = useState('');
  const [location, setLocation] = useState('');
  const [rating, setRating] = useState('');
  const [editing, setEditing] = useState(false);
  const [currentRestaurant, setCurrentRestaurant] = useState({});

  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    const response = await axios.get('http://localhost:8080/api/restaurants');
    setRestaurants(response.data);
  };

  const addRestaurant = async () => {
    const response = await axios.post('http://localhost:8080/api/restaurants', { name, location, rating });
    setRestaurants([...restaurants, response.data]);
    setName('');
    setLocation('');
    setRating('');
  };

  const updateRestaurant = async () => {
    const response = await axios.put(`http://localhost:8080/api/restaurants/${currentRestaurant.id}`, { name, location, rating });
    setRestaurants(restaurants.map(restaurant => (restaurant.id === currentRestaurant.id ? response.data : restaurant)));
    setName('');
    setLocation('');
    setRating('');
    setEditing(false);
  };

  const deleteRestaurant = async (id) => {
    await axios.delete(`http://localhost:8080/api/restaurants/${id}`);
    setRestaurants(restaurants.filter(restaurant => restaurant.id !== id));
  };

  const startEditing = (restaurant) => {
    setEditing(true);
    setName(restaurant.name);
    setLocation(restaurant.location);
    setRating(restaurant.rating);
    setCurrentRestaurant(restaurant);
  };

  return (
    <div>
      <h1>Restaurant List</h1>
      <div>
        <input
          type="text"
          placeholder="Restaurant Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          type="text"
          placeholder="Location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <input
          type="number"
          placeholder="Rating"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
        />
        {editing ? (
          <button onClick={updateRestaurant}>Update Restaurant</button>
        ) : (
          <button onClick={addRestaurant}>Add Restaurant</button>
        )}
      </div>
      <ul>
        {restaurants.map(restaurant => (
          <li key={restaurant.id}>
            {restaurant.name} - {restaurant.location} - {restaurant.rating} stars
            <button onClick={() => startEditing(restaurant)}>Edit</button>
            <button onClick={() => deleteRestaurant(restaurant.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default App;
