import React, { useState, useEffect } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container, Form, Row, Col, Button, Table } from 'react-bootstrap';
import MyNavbar from './components/Navbar';
import RestaurantEditModal from './components/RestaurantEditModal';
import './styles.css'; 

const App = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [name, setName] = useState('');
  const [location, setLocation] = useState('');
  const [rating, setRating] = useState('');
  const [editing, setEditing] = useState(false);
  const [currentRestaurant, setCurrentRestaurant] = useState({});
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    fetchRestaurants();
  }, []);

  const fetchRestaurants = async () => {
    const response = await axios.get('http://localhost:8080/api/restaurants');
    setRestaurants(response.data);
  };

  const addRestaurant = async () => {
    const response = await axios.post('http://localhost:8080/api/restaurants', { name, location, rating });
    setRestaurants([...restaurants, response.data]);
    setName('');
    setLocation('');
    setRating('');
  };

  const updateRestaurant = async (updatedRestaurant) => {
    const response = await axios.put(`http://localhost:8080/api/restaurants/${updatedRestaurant.id}`, updatedRestaurant);
    setRestaurants(restaurants.map(restaurant => (restaurant.id === updatedRestaurant.id ? response.data : restaurant)));
    setEditing(false);
  };

  const deleteRestaurant = async (id) => {
    await axios.delete(`http://localhost:8080/api/restaurants/${id}`);
    setRestaurants(restaurants.filter(restaurant => restaurant.id !== id));
  };

  const startEditing = (restaurant) => {
    setEditing(true);
    setCurrentRestaurant(restaurant);
    setShowModal(true);
  };

  const handleSave = (updatedRestaurant) => {
    updateRestaurant(updatedRestaurant);
    setShowModal(false);
  };

  const handleClose = () => {
    setShowModal(false);
  };

  return (
    <div>
      <MyNavbar />

      <Container className="mt-5">
        <h1 className="mb-4">Restaurant List</h1>
        <Form>
          <Form.Group as={Row} controlId="formName">
            <Form.Label column sm={2}>Restaurant Name</Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="Enter restaurant name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                style={{ fontSize: '18px', padding: '10px' }}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} controlId="formLocation">
            <Form.Label column sm={2}>Location</Form.Label>
            <Col sm={10}>
              <Form.Control
                type="text"
                placeholder="Enter location"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                style={{ fontSize: '18px', padding: '10px' }}
              />
            </Col>
          </Form.Group>

          <Form.Group as={Row} controlId="formRating">
            <Form.Label column sm={2}>Rating</Form.Label>
            <Col sm={10}>
              <Form.Control
                type="number"
                placeholder="Enter rating"
                value={rating}
                onChange={(e) => setRating(e.target.value)}
                style={{ fontSize: '18px', padding: '10px' }}
              />
            </Col>
          </Form.Group>

          <Button className="my-3" onClick={addRestaurant} variant="primary" size="lg">
            Add Restaurant
          </Button>
        </Form>

        <Table striped bordered hover className="mt-4 custom-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Location</th>
              <th>Rating</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {restaurants.map(restaurant => (
              <tr key={restaurant.id}>
                <td>{restaurant.name}</td>
                <td>{restaurant.location}</td>
                <td>{restaurant.rating}</td>
                <td>
                  <Button variant="warning" className="mr-2" onClick={() => startEditing(restaurant)}>Edit</Button>
                  <Button variant="danger" onClick={() => deleteRestaurant(restaurant.id)}>Delete</Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>

      <RestaurantEditModal
        show={showModal}
        handleClose={handleClose}
        restaurant={currentRestaurant}
        handleSave={handleSave}
      />
    </div>
  );
};

export default App;
