import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const RestaurantProfile = ({ restaurants }) => {
  const { id } = useParams(); // Get the restaurant ID from the URL parameters
  const [restaurant, setRestaurant] = useState(null); // Initially set to null

  useEffect(() => {
    // Find the restaurant with the matching ID and update the state
    const foundRestaurant = restaurants.find(r => r.id === parseInt(id));
    setRestaurant(foundRestaurant);
  }, [id, restaurants]); // Re-run effect if id or restaurants prop changes

  if (!restaurant) {
    return <div>Loading...</div>; // Placeholder while waiting for data
  }

  return (
    <div>
      <h2>{restaurant.name}</h2>
      <p>{restaurant.description}</p>
      {/* Render other restaurant details here */}
    </div>
  );
};

export default RestaurantProfile;
